// This is an IIFE also known as a javascript closure.
(function() {
	// your encapsilated code goes here.
	// 1. Removes your variables and functions from global scope.
	// 2. Avoids variable naming collisions
	// 3. Less likely to run into issues when minifying your javascript files.
})(); // <-- external paren



(function() {
	// Can also be written like this.
}()); // <-- nested paren